mapper.select:
select count(*)
from sys_dept
where parent_id = #{deptId}

update.foreach:
update sys_dept
set level = #{dept.level}
where id = #{dept.id}

mapper.select:
select 
from sys_dept
where level like #{level}

select count(*)
from sys_dept
where name = #{name}

  and parent_id = #{parentId}


  and id != #{id}

mapper.insert:
insert into sys_dept (id, name, parent_id, 
  level, seq, remark, 
  operator, operator_time, operator_ip
  )
values (#{id,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR}, #{parentId,jdbcType=INTEGER}, 
  #{level,jdbcType=VARCHAR}, #{seq,jdbcType=INTEGER}, #{remark,jdbcType=VARCHAR}, 
  #{operator,jdbcType=VARCHAR}, #{operatorTime,jdbcType=TIMESTAMP}, #{operatorIp,jdbcType=VARCHAR}
  )
